doc.example:
public function index() {

    $data = [

        'message' => 'Welcome ' . Auth::user()->name . '!',

        'user' => Auth::user()

    ];

    View::load('user/index', $data);

}

public function index() {

    View::load('template/header');

    View::load('user/index');

    View::load('template/footer');

}
